def RLE(vector):
    rle = []

    val = vector[0]
    length = 0
    for v in vector:
        if v == val:
            length += 1
        else:
            rle.append([val, length])
            val = v
            length = 1

    rle.append([val, length])

    return rle


def findReads(unpairedLens, pairedLens, lensLeft, lensRight, coverage, boundaries=None, debug=False):
    ''' Find the set of reads that most closely matches the distribution of readLens and the coverage vector
    '''

    fragmentLens = copy.copy(unpairedLens)
